import React, { useEffect, useReducer } from "react";
import reducer from "../Store/reducers/reducer";

export default () => {
  const [state, dispatch] = useReducer (reducer, ['user 1', 'user 2', 'user 3']);

  useEffect(() => {
    localStorage.setItem('users', JSON.stringify(state))
  }, state);

  return(
    <div>
      <h1>second page</h1>
      <div>Users:</div>
      {state.map(user => (
        <div>{user}</div>
      ))}
    </div>
  );
};
